refentry.refsect1:
&reftitle.description;
intfileperms stringfilename
指定したファイルのパーミッションを取得します。

&reftitle.parameters;




   filename



     ファイルへのパス。

  &reftitle.examples;



    八進形式でのパーミッションの表示


<?php
echo substr(sprintf('%o', fileperms('/tmp')), -4);
echo substr(sprintf('%o', fileperms('/etc/passwd')), -4);
?>


    &example.outputs;


1777
0644




    完全なパーミッションの表示


<?php
$perms = fileperms('/etc/passwd');

if (($perms & 0xC000) == 0xC000) {
    // ソケット
    $info = 's';
} elseif (($perms & 0xA000) == 0xA000) {
    // シンボリックリンク
    $info = 'l';
} elseif (($perms & 0x8000) == 0x8000) {
    // 通常のファイル
    $info = '-';
} elseif (($perms & 0x6000) == 0x6000) {
    // ブロックスペシャルファイル
    $info = 'b';
} elseif (($perms & 0x4000) == 0x4000) {
    // ディレクトリ
    $info = 'd';
} elseif (($perms & 0x2000) == 0x2000) {
    // キャラクタスペシャルファイル
    $info = 'c';
} elseif (($perms & 0x1000) == 0x1000) {
    // FIFO パイプ
    $info = 'p';
} else {
    // 不明
    $info = 'u';
}

// 所有者
$info .= (($perms & 0x0100) ? 'r' : '-');
$info .= (($perms & 0x0080) ? 'w' : '-');
$info .= (($perms & 0x0040) ?
            (($perms & 0x0800) ? 's' : 'x' ) :
            (($perms & 0x0800) ? 'S' : '-'));

// グループ
$info .= (($perms & 0x0020) ? 'r' : '-');
$info .= (($perms & 0x0010) ? 'w' : '-');
$info .= (($perms & 0x0008) ?
            (($perms & 0x0400) ? 's' : 'x' ) :
            (($perms & 0x0400) ? 'S' : '-'));

// 全体
$info .= (($perms & 0x0004) ? 'r' : '-');
$info .= (($perms & 0x0002) ? 'w' : '-');
$info .= (($perms & 0x0001) ?
            (($perms & 0x0200) ? 't' : 'x' ) :
            (($perms & 0x0200) ? 'T' : '-'));

echo $info;
?>


    &example.outputs;


