refentry.refsect1:
&reftitle.description;
&style.procedural;
mixedmaxdb_fetch_field_direct resourceresult intfieldnr
&style.oop;
mixedmaxdb_result::fetch_field_direct intfieldnr
maxdb_fetch_field_direct は、 指定した結果セットのフィールド定義情報を含むリソースを返します。 fieldnr の値は、0 から フィールド数 - 1 の間でなければなりません。

  &reftitle.examples;

   &style.oop;


<?php
$maxdb = new maxdb("localhost", "MONA", "RED", "DEMODB");

/* 接続を調べます */
if (maxdb_connect_errno()) {
   printf("接続に失敗しました: %s\n", maxdb_connect_error());
   exit();
}

$query = "SELECT name, cno from hotel.customer ORDER BY name";

if ($result = $maxdb->query($query)) {

   /* 'SurfaceArea' のフィールド情報を取得します */
   $finfo = $result->fetch_field_direct(1);

   printf("Name:     %s\n", $finfo->name);
   printf("Table:    %s\n", $finfo->table);
   printf("max. Len: %d\n", $finfo->max_length);
   printf("Flags:    %d\n", $finfo->flags);
   printf("Type:     %d\n", $finfo->type);

   $result->close();
}

/* 接続を閉じます */
$maxdb->close();
?>




   &style.procedural;


<?php
$link = maxdb_connect("localhost", "MONA", "RED", "DEMODB");

/* 接続を調べます */
if (maxdb_connect_errno()) {
   printf("接続に失敗しました: %s\n", maxdb_connect_error());
   exit();
}

$query = "SELECT name, cno from hotel.customer ORDER BY name";

if ($result = maxdb_query($link, $query)) {

   /* 'SurfaceArea' のフィールド情報を取得します */
   $finfo = maxdb_fetch_field_direct($result, 1);

   printf("Name:     %s\n", $finfo->name);
   printf("Table:    %s\n", $finfo->table);
   printf("max. Len: %d\n", $finfo->max_length);
   printf("Flags:    %d\n", $finfo->flags);
   printf("Type:     %d\n", $finfo->type);

   maxdb_free_result($result);
}

/* 接続を閉じます */
maxdb_close($link);
?>



  &example.outputs.similar;


