refentry.refsect1:
  &reftitle.examples;




     session_set_save_handler で SessionHandlerInterface を使う例



<?php
class MySessionHandler implements SessionHandlerInterface
{
    private $savePath;

    public function open($savePath, $sessionName)
    {
        $this->savePath = $savePath;

        return true;
    }

    public function close()
    {
        return true;
    }

    public function read($id)
    {
        $file = "$this->savePath/sess_$id";
        return (string)@file_get_contents($file);
    }

    public function write($id, $data)
    {
        $file = "$this->savePath/sess_$id";
        if ($fp = @fopen($file, "w")) {
            $return = fwrite($fp, $data);
            fclose($fp);
            return $return;
        } else {
            return false;
        }
    }

    public function destroy($id)
    {
        $file = "$this->savePath/sess_$id";
        if (file_exists($file)) {
            unlink($file);
        }

        return true;
    }

    public function gc($maxlifetime)
    {
        foreach (glob("$this->savePath/sess_*") as $file) {
            if (filemtime($file) + $maxlifetime < time() && file_exists($file)) {
                unlink($file);
            }
        }

        return true;
    }
}

$handler = new MySessionHandler();
session_set_save_handler($handler);
session_start();

// $_SESSION への値の設定や格納されている値の取得を進めます




    session_set_save_handler の例


     以下の例では、デフォルトの保存 files に似た
     ファイルベースのセッション保存を行います。この例は、PHP がサポート
     する任意のデータベースエンジンを用いてデータベースへの保存を行う
     ように容易に拡張可能です。



<?php
function open($savePath, $sessionName)
{
    global $sess_save_path;

    $sess_save_path = $savePath;

    return true;
}

function close()
{
    return true;
}

function read($id)
{
    global $sess_save_path;

    $sess_file = "$sess_save_path/sess_$id";

    return (string)@file_get_contents($sess_file);
}

function write($id, $data)
{
    global $sess_save_path;

    $sess_file = "$sess_save_path/sess_$id";
    if ($fp = @fopen($sess_file, "w")) {
        $return = fwrite($fp, $data);
        fclose($fp);

        return $return;
    } else {
        return false;
    }
}

function destroy($id)
{
    global $sess_save_path;

    $file = "$sess_save_path/sess_$id";
    if (file_exists($file)) {
        unlink($file);
    }
    return true;
}

function gc($maxlifetime)
{
    global $sess_save_path;

    foreach (glob("$sess_save_path/sess_*") as $file) {
        if (filemtime($file) + $maxlifetime < time() && file_exists($file)) {
            unlink($file);
        }
    }

    return true;
}

session_set_save_handler("open", "close", "read", "write", "destroy", "gc");

session_start();
// $_SESSION への値の設定や格納されている値の取得を進めます

